iterations.oclString:
self.out_REQs->forAll(req | 
    req.state = 'released' implies (
        req.successorItems->exists(review : azure_workitem | 
            review.workItemType = 'Review' and 
            not review.relatedItems->exists(finding : azure_workitem | 
                finding.workItemType = 'Reviewfinding' and 
                finding.state = 'open'
            )
        )
    )
)
